import React from 'react';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import SkipPreviousIcon from '@material-ui/icons/SkipPrevious';
import PlayArrowIcon from '@material-ui/icons/PlayArrow';
import SkipNextIcon from '@material-ui/icons/SkipNext';
import { borders } from '@material-ui/system';

const useStyles = makeStyles(theme => ({
  card: {
    display: 'flex',
    height: 500,
  },
  details: {
    display: 'flex',
    flexDirection: 'column',
  },
  content: {
    flex: '1 0 auto',
  },
  text: {
    width: 500,
    height: 400,
    marginRight: 200,
  },
  cover: {
    width: 1000,
  },
  controls: {
    display: 'flex',
    alignItems: 'center',
    paddingLeft: theme.spacing(1),
    paddingBottom: theme.spacing(1),
  },
  playIcon: {
    height: 38,
    width: 38,
  },
}));

export default function MediaControlCard() {
  const classes = useStyles();
  const theme = useTheme();

  return (
    <Card className={classes.card} borderRadius={50}>
      <div className={classes.details}>

        <div className={classes.text}>
          <CardContent className={classes.content}>
            <Typography component="h5" variant="h5">

              J. Allan Hobson, Professor of Psychiatry <br />

              Harvard Medical School
              <br />
              <br />

            </Typography>
            <Typography variant="subtitle1" >
              Allan Hobson is an experimentalist with a broad range of interests.
      As a scientist, he has conducted pioneering investigations on the brainstem mechanisms of sleep and dreaming which have revolutionized dream theory.
      His strong interest in the arts led him to design the award-winning scientific exhibit called Dreamstage.
          </Typography>
          </CardContent>

        </div>

        <div className={classes.controls}>
          <IconButton aria-label="Previous">
            {theme.direction === 'rtl' ? <SkipNextIcon /> : <SkipPreviousIcon />}
          </IconButton>
          <IconButton aria-label="Play/pause">
            <PlayArrowIcon className={classes.playIcon} />
          </IconButton>
          <IconButton aria-label="Next">
            {theme.direction === 'rtl' ? <SkipPreviousIcon /> : <SkipNextIcon />}
          </IconButton>
        </div>
      </div>
      <CardMedia
        className={classes.cover}
        image="http://allanhobson.org/index/lobsterallan.jpg"
        title="Live from space album cover"
      />
    </Card>

  );
}
