import React from 'react';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import bk from '../assets/bk1.jpg';
import Grid from '@material-ui/core/Grid';


const useStyles = makeStyles(theme => ({
    card: {
        display: 'flex',
        height: 300,
    },

    cover: {
        height: 300,
        width: 200,
        alignContent: 'right',

    },
    title: {
        fontFamily: 'Source Serif Pro, serif',
        fontSize: 22,
        flexGrow: 1,
        color: '#00363d',
        fontWeight: 600,
        textAlign: 'left',
        paddingLeft: 25,
    },
    body: {
        fontFamily: 'Roboto, sans-serif',
        fontSize: 16,
        color: '#333333',
        fontWeight: 400,
        textAlign: 'justify',
        textJustify: 'inter-word',
        lineHeight: 25,
        padding: 25,
    }

}));

export default function Bookcard() {
    const classes = useStyles();
    const theme = useTheme();

    return (
        <Card className={classes.card}>
            <Grid item xs={9}>
                <div className={classes.details}>
                    <CardContent className={classes.content}>
                        <div className={classes.title}>
                            The Dreaming Brain
                        </div>
                        <Typography className={classes.body}>
                            <p>
                                This is the foundational text which contrasts Hobson's new theory of dreaming with that of Freud.
                                Paperback version is still in print and available in bookstores.
                                 "[Hobson] provides a basis for a formal analysis of dreams as behavior that is not only valuable as a
                                 contribution to the field but is critical if we are to proceed in the principal challenge of neurobiology,
                                 the understanding of the human mind.." -- from a review by Robert Y. Moore
                            </p>

                        </Typography>
                    </CardContent>

                </div>
            </Grid>
            <Grid item xs={3}>
                <CardMedia
                    className={classes.cover}
                    image={bk}
                    title="book 1"
                />
            </Grid>

        </Card>
    );
}
