import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import ButtonGroup from '@material-ui/core/ButtonGroup';
import Grid from '@material-ui/core/Grid';
import Container from '@material-ui/core/Container';

const useStyles = makeStyles(({

  card: {
    display: 'flex',
    height: 500,
    borderRadius: 0,
  },
  details: {
    display: 'flex',
    flexDirection: 'column',
  },
  content: {
    flex: '1 0 auto',
  },
  text: {
    width: 500,
    height: 400,
    marginRight: 200,
    padding: 20,
  },
  cover: {
    width: 1000,
  },
  controls: {
    display: 'flex',
    padding: 30,
    marginRight: 20,
  },

}));

export default function Hero() {
  const classes = useStyles();

  return (

    <Container fixed>

      <div className={classes.root}>
        <Grid container spacing={3}>

          <Grid item xs={6}>

            <Typography component="h5" variant="h5">

              J. Allan Hobson, Professor of Psychiatry <br />

              Harvard Medical School
              <br />
              <br />

            </Typography>
            <Typography variant="subtitle1" >
              Allan Hobson is an experimentalist with a broad range of interests.
              As a scientist, he has conducted pioneering investigations on the brainstem mechanisms of sleep and dreaming which have revolutionized dream theory.
              His strong interest in the arts led him to design the award-winning scientific exhibit called Dreamstage.
            </Typography>

            <ButtonGroup fullWidth aria-label="Full width outlined button group">
              <Button>Books</Button>
              <Button>Museum</Button>
            </ButtonGroup>

          </Grid>
          <Grid item xs={6}>

          </Grid>

        </Grid>
      </div>



















      <Card className={classes.card} >
        <div className={classes.details}>

          <div className={classes.text}>
            <CardContent className={classes.content}>
              <Typography component="h5" variant="h5">

                J. Allan Hobson, Professor of Psychiatry <br />

                Harvard Medical School
              <br />
                <br />

              </Typography>
              <Typography variant="subtitle1" >
                Allan Hobson is an experimentalist with a broad range of interests.
        As a scientist, he has conducted pioneering investigations on the brainstem mechanisms of sleep and dreaming which have revolutionized dream theory.
        His strong interest in the arts led him to design the award-winning scientific exhibit called Dreamstage.
          </Typography>
              <ButtonGroup fullWidth aria-label="Full width outlined button group">
                <Button>Books</Button>
                <Button>Museum</Button>
              </ButtonGroup>
            </CardContent>


          </div>

          <div className={classes.controls}>


          </div>
        </div>
        <CardMedia
        />
        <img className={classes.cover} src="http://allanhobson.org/index/lobsterallan.jpg" alt="allan-hobson"></img>
      </Card>
    </Container>

  );
}
