import React, { Component } from 'react';
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';
import Divider from '@material-ui/core/Divider';
import Container from '@material-ui/core/Container';
import Typography from '@material-ui/core/Typography';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';

export default class Home extends Component {




    render() {
        return (
            <div>
                <Carousel
                    additionalTransfrom={0}
                    arrows
                    autoPlay
                    autoPlaySpeed={3000}
                    centerMode={false}
                    containerClass="container"
                    dotListClass=""
                    draggable
                    focusOnSelect={false}
                    infinite
                    itemClass=""
                    keyBoardControl
                    minimumTouchDrag={80}
                    responsive={{
                        desktop: {
                            breakpoint: {
                                max: 3000,
                                min: 1024
                            },
                            items: 1
                        },
                        mobile: {
                            breakpoint: {
                                max: 464,
                                min: 0
                            },
                            items: 1
                        },
                        tablet: {
                            breakpoint: {
                                max: 1024,
                                min: 464
                            },
                            items: 1
                        }
                    }}
                    showDots
                    sliderClass=""
                    slidesToSlide={1}
                    swipeable
                >
                    <img
                        src="http://allanhobson.org/images/M04_3.JPG"
                        alt="slide2"
                        style={{
                            display: 'block',
                            height: '600px',
                            margin: 'auto',
                            width: '1000px'
                        }}
                    />
                    <img
                        src="http://allanhobson.org/images/M04_4.JPG"
                        alt="slide3"
                        style={{
                            display: 'block',
                            height: '600px',
                            margin: 'auto',
                            width: '1000px'
                        }}
                    />
                    <img
                        src="http://allanhobson.org/images/M04_8.JPG"
                        alt="slide4"
                        style={{
                            display: 'block',
                            height: '600px',
                            margin: 'auto',
                            width: '1000px'
                        }}
                    />
                    <img
                        src="http://allanhobson.org/index/lobsterallan.jpg"
                        alt="slide5"
                        style={{
                            display: 'block',
                            height: '600px',
                            margin: 'auto',
                            width: '1000px'
                        }}
                    />
                </Carousel>
                <Divider />

                <div  >
                    <Container maxWidth="lg">
                        <Typography style={{
                            fontSize: '25px',
                            textAlign: 'center',
                            fontFamily: 'Palanquin Dark',
                            marginTop: '50px'


                        }}>
                            Welcome to Allan Hobson's Personal Website
                     </Typography>

                        <Container maxWidth="md">
                            <Typography style={{
                                fontSize: '18px',
                                textAlign: 'justify',
                                marginTop: '30px',
                                textJustify: 'inter-character',


                            }}>
                                Allan Hobson is an experimentalist with a broad range of interests.As a scientist, he has conducted pioneering investigations on the brainstem
                                mechanisms of sleep and dreaming which have revolutionized dream theory.  His strong interest in the arts led him to design the award-winning
                                scientific exhibit called Dreamstage.
                                <br />
                                <br />

                                As a psychiatrist he has championed a direct and interpersonal approach to psychotherapy with as much emphasis on adaptive behavior as
                                on biographical interpretation.
                                <br />
                                <br />
                                In his personal life, he has experimented by creating a group of friends to compensate for the relative weakness of the extended family
                                in the USA. He has also developed numerous intimate relationships to compensate for the shortcomings of institutional relationships.
                                <br />
                                <br />
                                On his dairy farm in East Burke, Vermont, he has experimented in the design and restoration of 8 antique buildings.
                                The dairy barn, built in 1860, now houses a new Dreamstage exhibition, recreated laboratory office, and an installed silo library.
                                 He is now building the Art of Life art gallery.
                                 <br />
                                <br />
                                Allan Hobson lives half-time with his new wife, Rosalia Silvestri, and their twin sons, Andrew and Matthew, in Messina, Sicily.
                                With his first wife, Joan Harlowe, he fathered 3 children: Ian, Christopher, and Julia. He has four grandchildren, Henry, Lucy, Chance and Jude.
                                 <br />
                                <br />
                            </Typography>
                            <br />
                            <br />
                            <Divider />
                        </Container>


                    </Container>

                    <Container maxWidth="md">
                        <Typography style={{
                            fontSize: '25px',
                            textAlign: 'center',
                            fontFamily: 'Palanquin Dark',
                            marginTop: '50px'


                        }}>
                            Allan Hobson's Books
                     </Typography>

                        <Card className={classes.card}>
                            <CardActionArea>
                                <CardMedia
                                    component="img"
                                    alt="Contemplative Reptile"
                                    height="140"
                                    image="/static/images/cards/contemplative-reptile.jpg"
                                    title="Contemplative Reptile"
                                />
                                <CardContent>
                                    <Typography gutterBottom variant="h5" component="h2">
                                        Lizard
                                    </Typography>
                                    <Typography variant="body2" color="textSecondary" component="p">
                                        Lizards are a widespread group of squamate reptiles, with over 6,000 species, ranging
                                        across all continents except Antarctica
                                 </Typography>
                                </CardContent>
                            </CardActionArea>
                            <CardActions>
                                <Button size="small" color="primary">
                                    Share
                              </Button>
                                <Button size="small" color="primary">
                                    Learn More
                              </Button>
                            </CardActions>
                        </Card>
                    </Container>
                </div>




            </div>
        )
    }
}
